parameters.inputSchema:
{
  "type": "OBJECT",
  "properties": {
    "summary": {
      "type": "STRING",
      "description": "A concise, single-paragraph summary of the meeting's overall discussion and outcome."
    },
    "tasks": {
      "type": "ARRAY",
      "items": {
        "type": "OBJECT",
        "properties": {
          "taskName": {
            "type": "STRING",
            "description": "The specific actionable task extracted from the transcript."
          },
          "assignee": {
            "type": "STRING",
            "description": "The person assigned to the task, or 'Unassigned'."
          },
          "deadline": {
            "type": "STRING",
            "description": "The target date for completion in YYYY-MM-DD format, or 'TBD' if not specified."
          }
        },
        "required": ["taskName", "assignee", "deadline"]
      }
    }
  },
  "required": ["summary", "tasks"]
}
